% 0、图像二值化I = imread('lena.png');imggray = rgb2gray(I);level = graythresh(imggray);BW = im2bw(imggray,level);figure (2)imshow(BW);hold on% 1、hough：输入二值图像BW，角度范围与步进（最大，[-90, 90)）,返回 H-霍夫空间，T-theta，R-p；[H,T,R] = hough(BW,'Theta',20:0.1:75); % 2、houghpeaks：输入霍夫空间和极值数量，返回极值的坐标PEAKS = houghpeaks(H,5); % NUMPEAKS = 5% PEAKS = houghpeaks(H,5,'threshold',ceil(0.3*max(H(:))));% 3、houghlines：返回lines是一个包含图像中线段首末点、p、theta的结构体LINES = houghlines(BW,T,R,PEAKS); % LINES = houghlines(BW,T,R,PEAKS,'FillGap',5,'MinLength',7); % 4、用plot函数逐点连接出来for k=1:length(LINES)     xy=[LINES(k).point1;LINES(k).point2];     plot(xy(:,1),xy(:,2),'LineWidth',4); end